import React from 'react';
import { Grid, Card, CardContent, CardMedia, Typography, Button } from '@mui/material';
import { useNavigate } from 'react-router-dom';

const pets = [
  { id: 'chillturtle', name: 'Chill Turtle', emoji: '🐢', description: 'The most relaxed pet in the zoo' },
  { id: 'emoocto', name: 'Emo Octo', emoji: '🐙', description: 'Moody but lovable' },
  { id: 'chaosdragon', name: 'Chaos Dragon', emoji: '🐉', description: 'Brings the fun and chaos' },
  { id: 'babydino', name: 'Baby Dino', emoji: '🦖', description: 'Small but mighty' },
  { id: 'bouncybun', name: 'Bouncy Bun', emoji: '🐇', description: 'Always full of energy' },
];

const PetSelection: React.FC = () => {
  const navigate = useNavigate();

  return (
    <Grid container spacing={4}>
      {pets.map((pet) => (
        <Grid item xs={12} sm={6} md={4} key={pet.id}>
          <Card sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
            <CardMedia
              component="div"
              sx={{
                pt: '56.25%',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                fontSize: '4rem',
              }}
            >
              {pet.emoji}
            </CardMedia>
            <CardContent sx={{ flexGrow: 1 }}>
              <Typography gutterBottom variant="h5" component="h2">
                {pet.name}
              </Typography>
              <Typography>{pet.description}</Typography>
            </CardContent>
            <Button
              size="large"
              color="primary"
              onClick={() => navigate(`/pet/${pet.id}`)}
              sx={{ m: 2 }}
            >
              Play with {pet.name}
            </Button>
          </Card>
        </Grid>
      ))}
    </Grid>
  );
};

export default PetSelection;
